import React from 'react';
import { useNavigate } from 'react-router-dom'; // Import useNavigate
import './Offers.css';
import exclusive_image from '../Assets/exclusive_image.png';

const Offers = () => {
    const navigate = useNavigate(); // Initialize navigate

    const handleCheckNowClick = () => {
        navigate('/women'); // Navigate to the women section
    };

    return (
        <div className='offers'>
            <div className='offers-left'>
                <h1>Exclusive Offers</h1>
                <h1>Offers For All</h1>
                <p>The Best Selling Products</p>
                <button onClick={handleCheckNowClick}>Check Now</button> {/* Add click handler */}
            </div>
            <div className='offers-right'>
                <img src={exclusive_image} alt="Exclusive Offers" />
            </div>
        </div>
    );
};

export default Offers;
